    public void actualiza_desc_bono(){
        Row [] vDesc;
        vDesc=this.getSisDescuentoReintegroVw().getAllRowsInRange();
        Number vDesc1,vBono1,vTemp;
        int vTam;
        
        vTam=vDesc.length;
        vDesc1=utils.getNumberOracle("0");
        vBono1=utils.getNumberOracle("0");
        
       try{
          
        for (int i=0;i<vTam;i++){
            
            vTemp=(Number)vDesc[i].getAttribute("ValorAplicado");
            
             if (vTemp.compareTo(0)<0){
                 vDesc1=vDesc1.add(vTemp);  
             }else{
                 vBono1=vBono1.add(vTemp);
             }
            
        }
        
           
        vDesc1=new Number(vDesc1.abs());
        vBono1=new Number(vBono1.abs());
          
        this.setTotalDescuentos(vDesc1);
          
        this.setTotalBono(vBono1); 
           
        this.setLiquido(this.getNominal().subtract(vDesc1).add(vBono1));
           
       }catch(Exception exep){
           throw new JboException("Ocurrio un error al actualizar los totales de bono, descuentos y liquido.");
       }
       
    }

