   public boolean verificar_doc_pago_Anular(Number pIdPago){
   
       Object  vResultado;      
       vResultado=utils_DB.getValorFuncion(this.getDBTransaction(),"sis_pkg_pagos.CHECK_ANUL_PAGO("+pIdPago +")",null); 
       
       if (vResultado!=null){
           
          if (vResultado.toString().equals("ER")){
          
              throw new JboException("El pago no se puede anular en estados GENERADO, PAGADO Y ANULADO");
              
          }else if(vResultado.toString().equals("ER_ACRE")){
          
              throw new JboException("El acreditamiento no se puede anular, porque ya fue generado el archivo final");
              
          }else if(vResultado.toString().equals("ER_CHEQ")){
          
              throw new JboException("El cheque no se puede anular, porque ya fue PreAnulado.");
              
          }else if(vResultado.toString().equals("ER_ACRE_A")){
          
              throw new JboException("El acreditamiento no se puede anular, porque ya fue PreAnulado.");
              
          }else if (vResultado.toString().contains("ER")){
          
              throw new JboException("No se puede anular el pago porfavor verifique.");              
          }
           
       }else{
           
           throw new JboException("El pago no se puede anular"); 
       }
    return true;   
   }
        
